mapper.sql:
WHERE TB.DELETEFLAG='N' AND TBG.DELETEFLAG='N'

    AND TB.BGNO=#{bgno}



         ${item} LIKE CONCAT('%', #{searchKeyword},'%' )

mapper.select:
SELECT BRDNO, BRDTITLE, DATE_FORMAT(TB.REGDATE,'%Y-%m-%d') as REGDATE, BRDNOTICE, BRDLIKE , TB.USERNO, USERNM BRDWRITER, BGNAME, TB.BGNO , (SELECT COUNT(*) FROM TBL_BOARDREAD WHERE BRDNO=TB.BRDNO) BRDHIT , (SELECT COUNT(*) FROM TBL_BOARDFILE WHERE BRDNO=TB.BRDNO AND DELETEFLAG = 'N') FILECNT , (SELECT COUNT(*) FROM TBL_BOARDREPLY WHERE BRDNO=TB.BRDNO AND REDELETEFLAG='N') REPLYCNT FROM TBL_BOARD TB INNER JOIN COM_USER CU ON TB.USERNO=CU.USERNO INNER JOIN TBL_BOARDGROUP TBG ON TBG.BGNO=TB.BGNO WHERE TB.DELETEFLAG='N' AND BRDNOTICE='Y'
AND TB.BGNO=#{bgno}
ORDER BY BRDNO DESC LIMIT 3

mapper.update:
UPDATE TBL_BOARD
   SET BRDTITLE=#{brdtitle}
     , BRDMEMO=#{brdmemo} 
     , BRDNOTICE=#{brdnotice}
     , CHGDATE=NOW()
     , CHGUSERNO=#{userno}
     , ETC1=#{etc1, jdbcType=VARCHAR}
     , ETC2=#{etc2, jdbcType=VARCHAR}
     , ETC3=#{etc3, jdbcType=VARCHAR}
     , ETC4=#{etc4, jdbcType=VARCHAR}
     , ETC5=#{etc5, jdbcType=VARCHAR}
 WHERE DELETEFLAG='N'
   AND BRDNO=#{brdno}

mapper.insert:
UPDATE TBL_BOARD
   SET BRDLIKE = BRDLIKE + 1 
 WHERE BRDNO=#{field1}

mapper.delete:
UPDATE TBL_BOARD
   SET DELETEFLAG='Y'
     , CHGDATE=NOW()
     , CHGUSERNO=#{userno}
 WHERE BRDNO=#{brdno}

mapper.update:
UPDATE TBL_BOARDFILE SET DELETEFLAG = 'Y'
WHERE FILENO IN (

    ${item}

)

mapper.delete:
DELETE
  FROM TBL_BOARDFILE
 WHERE FILENO IN (

             ${item}

)

mapper.insert:
INSERT INTO TBL_BOARDREPLY(BRDNO, USERNO, REDELETEFLAG, REMEMO, REGDATE, CHGDATE, CHGUSERNO, REORDER, REPARENT, REDEPTH)
VALUES (#{brdno}, #{userno}, 'N', #{rememo}, NOW(), NOW(), #{userno}, #{reorder},

               #{reno}, 0
               #{reparent}, #{redepth}

       )

    SELECT LAST_INSERT_ID()

mapper.update:

UPDATE TBL_BOARDREPLY
   SET REORDER = REORDER + 1
 WHERE BRDNO=#{brdno} AND REORDER > #{reorder}

mapper.select:
SELECT IFNULL(MAX(REORDER),0)+1
  FROM TBL_BOARDREPLY
 WHERE BRDNO=#{brdno}

mapper.update:
UPDATE TBL_BOARDREPLY
   SET REDELETEFLAG='Y' 
     , CHGDATE=NOW()
     , CHGUSERNO=#{userno}
 WHERE RENO=#{reno}

UPDATE TBL_BOARDREPLY
SET REDELETEFLAG='Y'
  , CHGDATE=NOW()
  , CHGUSERNO=#{userno}
WHERE BRDNO=#{brdno}

UPDATE TBL_BOARDREPLY
   SET REMEMO=#{rememo} 
     , CHGDATE=NOW()
     , CHGUSERNO=#{userno}
 WHERE RENO=#{reno}
