mapper.insert:
INSERT INTO COM_DATE (
    CDDATE, CDYEAR, CDMM, CDDD, CDWEEKOFYEAR, CDWEEKOFMONTH, CDWEEK, CDDAYOFWEEK
) VALUES (
    #{date}, #{year}, #{month}, #{day}, #{weekOfYear}, #{weekOfMonth}, #{week}, #{dayOfWeek}
)

    SELECT LAST_INSERT_ID()

mapper.select:
SELECT COUNT(*) > 0
FROM COM_DATE
WHERE CDDATE = #{date}
