mapper.select:
SELECT PRNO, PRTITLE, REGDATE, PRSTARTDATE, PRENDDATE , PP.USERNO, USERNM, CODENM as PRSTATUS FROM PRJ_PROJECT PP INNER JOIN COM_USER CU ON PP.USERNO=CU.USERNO INNER JOIN COM_CODE CC ON PP.PRSTATUS=CC.CODECD
AND CC.CLASSNO=2 ORDER BY PRNO DESC
LIMIT ${rowStart-1}, ${displayRowCount}

mapper.update:
UPDATE PRJ_PROJECT
   SET PRTITLE=#{prtitle}
     , PRSTATUS=#{prstatus}
     , PRSTARTDATE=#{prstartdate} 
     , PRENDDATE=#{prenddate}
 WHERE DELETEFLAG='N'
   AND PRNO=#{prno}

mapper.delete:
UPDATE PRJ_PROJECT
   SET DELETEFLAG='Y'
 WHERE PRNO=#{prno}

mapper.update:
UPDATE PRJ_TASK PT1
 INNER JOIN PRJ_TASK PT2 ON PT2.PRNO=PT1.PRNO AND PT2.OLDNO=PT1.TSPARENT
   SET PT1.TSPARENT=PT2.TSNO 
 WHERE PT1.DELETEFLAG='N' AND PT1.PRNO=#{prno}
   AND PT2.DELETEFLAG='N' AND PT2.PRNO=#{prno}
