mapper.update:
UPDATE TBL_BOARDGROUP
SET BGNAME=#{bgname}
  , BGUSED=#{bgused}
  , BGREPLY=#{bgreply}
  , BGREADONLY=#{bgreadonly}
  , BGNOTICE=#{bgnotice}
  , CHGDATE=SYSDATE
WHERE BGNO = #{bgno}

mapper.select:
SELECT BGNO, BGNAME, BGUSED, BGREPLY, BGREADONLY, BGNOTICE
FROM TBL_BOARDGROUP
WHERE DELETEFLAG='N' AND BGNO=#{bgno}

mapper.delete:
UPDATE TBL_BOARDGROUP
SET DELETEFLAG='Y', CHGDATE=SYSDATE
WHERE BGNO=#{bgno}
