mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY CLASSNO, CODECD) AS RNUM , CLASSNO, CODECD, CODENM FROM COM_CODE TB
)
WHERE RNUM BETWEEN ${rowStart} AND ${rowStart-1}+${displayRowCount} -- OFFSET ${rowStart-1} ROWS FETCH FIRST ${displayRowCount} ROWS ONLY -- oracle12c

mapper.update:
UPDATE COM_CODE
   SET CODENM=#{codenm}
 WHERE CLASSNO=#{classno} AND CODECD=#{codecd}

mapper.select:
SELECT CLASSNO, CODECD, CODENM
  FROM COM_CODE CC
 WHERE CLASSNO=#{classno} AND CODECD=#{codecd}
