mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY SD.DOCNO DESC) AS RNUM , SD.DOCNO, DOCTITLE, SD.USERNO, USERNM, DOCSTEP, SD.DTNO, SDT.DTTITLE, DEPTNM , TO_CHAR(CHGDATE,'YYYY-MM-DD') CHGDATE, CODENM DOCSTATUS FROM SGN_DOC SD INNER JOIN COM_USER CU ON SD.USERNO=CU.USERNO INNER JOIN SGN_DOCTYPE SDT ON SDT.DTNO=SD.DTNO INNER JOIN COM_CODE CC ON CC.CODECD=SD.DOCSTATUS AND CLASSNO='2'
)
WHERE RNUM BETWEEN ${rowStart} AND ${rowStart-1}+${displayRowCount} -- OFFSET ${rowStart-1} ROWS FETCH FIRST ${displayRowCount} ROWS ONLY -- oracle12c

mapper.sql:
WHERE SD.DELETEFLAG='N' AND SD.USERNO=${userno}

    AND SD.DOCSTATUS IN (1,2)
    AND SD.DOCSTATUS=${searchExt1}




         ${item} LIKE '%'||#{searchKeyword}||'%'

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY DOCNO DESC) AS RNUM , SD.DOCNO, DOCTITLE, SD.USERNO, USERNM, DOCSTEP, SD.DTNO, SDT.DTTITLE, DEPTNM , TO_CHAR(CHGDATE,'YYYY-MM-DD') CHGDATE, CODENM DOCSTATUS FROM SGN_DOC SD INNER JOIN COM_USER CU ON SD.USERNO=CU.USERNO INNER JOIN SGN_DOCTYPE SDT ON SDT.DTNO=SD.DTNO INNER JOIN COM_CODE CC ON CC.CODECD=SD.DOCSTATUS AND CLASSNO='2'
)
WHERE RNUM BETWEEN ${rowStart} AND ${rowStart-1}+${displayRowCount} -- OFFSET ${rowStart-1} ROWS FETCH FIRST ${displayRowCount} ROWS ONLY -- oracle12c

mapper.update:
UPDATE SGN_DOC
   SET DOCTITLE=#{doctitle}, DOCCONTENTS=#{doccontents}, DOCSIGNPATH=#{docsignpath}
     , CHGDATE=SYSDATE
 WHERE DOCNO=#{docno}

UPDATE SGN_DOC
   SET DELETEFLAG='Y'
 WHERE DOCNO=#{docno}

UPDATE SGN_DOC
   SET DOCSTATUS = #{ssresult}

        , DOCSTEP = DOCSTEP + 1

 WHERE DOCNO=#{docno}

UPDATE SGN_DOC
   SET DOCSTATUS = 0
 WHERE DOCNO=#{docno}
